// src/components/ShowDetails.js

import React, { useState, useEffect } from 'react';
import { getShowSummary } from '../api';

const ShowDetails = ({ showId }) => {
  const [summary, setSummary] = useState('');

  useEffect(() => {
    const fetchSummary = async () => {
      const showSummary = await getShowSummary(showId);
      setSummary(showSummary);
    };

    fetchSummary();
  }, [showId]);

  return (
    <div>
      <h2>Show Details</h2>
      <p>{summary}</p>
    </div>
  );
};

export default ShowDetails;
