import logo from './logo.svg';
import './App.css';
import React, { useState } from 'react';
import ShowList from './components/ShowList';
import ShowDetails from './components/ShowDetails';
import ShowSummary from './components/ShowSummary';
import MovieBookingForm from './components/MovieBookingForm';

const App = () => {
  const [selectedShow, setSelectedShow] = useState(null);

  const handleShowSelect = (showId) => {
    setSelectedShow(showId);
  };

  return (
    <div>
      {!selectedShow && <ShowList onSelectShow={handleShowSelect} />}
      {selectedShow && (
        <>
          <ShowDetails showId={selectedShow} />
          <ShowSummary showId={selectedShow} />
          <MovieBookingForm showId={selectedShow} />
        </>
      )}
    </div>
  );
};

export default App;
