// src/components/MovieBookingForm.js

import React, { useState } from 'react';

const MovieBookingForm = ({ showId }) => {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');

  const handleNameChange = (event) => {
    setName(event.target.value);
  };

  const handleEmailChange = (event) => {
    setEmail(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    // Perform your form submission logic here, e.g., API call, etc.
    // You can use the 'name', 'email', and 'showId' state values for your submission.
  };

  return (
    <div>
      <h2>Movie Booking Form</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="name">Name:</label>
          <input type="text" id="name" value={name} onChange={handleNameChange} required />
        </div>
        <div>
          <label htmlFor="email">Email:</label>
          <input type="email" id="email" value={email} onChange={handleEmailChange} required />
        </div>
        <div>
          <label htmlFor="showId">Show ID:</label>
          <input type="text" id="showId" value={showId} readOnly />
        </div>
        <button type="submit">Book Movie</button>
      </form>
    </div>
  );
};

export default MovieBookingForm;
